import React, { useState } from 'react';
import { Dropdown } from 'semantic-ui-react';
import './App.css';
import { getCities } from './helper';
import styled from 'styled-components';

// const SearchBar = styled.input`
//   width: 45%;
//   background-color: white;
//   position: absolute:
//   top: 40%;
//   min-width: 200px;
//   border: none;
//   border-radius: 3px 0 0 3px;
//   padding: 20px;
//   :focus{
//     outline: none;
//   }
// `;

const SearchButton = styled.button`
  width: 15%;
  background-color: #e8415d;
  position: absolute:
  top: 40%;
  padding-left: 10px;
  border: none;
  min-width: 100px;
  display: block;
  font-color: white;
  border-radius: 2px 2px 0;
`;

function App() {
  const [ cityName, setCityName ] = useState('');
  const [ cityList, setCityList ] = useState([]);
  const [ searchedCities, setSearchedCities ] = useState([]);

  // const dropdown = document.getElementById('cityname');

  const getCitiesData = () => {     
    if(cityName.length > 1) {
      getCities(cityName.toLowerCase()).then(res => {
        setCityList(res);
    //     res.map(city => {
    //       var el = document.createElement("option");
    // el.textContent = city;
    // el.value = city;
    // dropdown.appendChild(el);}
    // )
      }).catch(err => console.log(err));
    }
  }

  const onSubmit = () => {
    if(cityName.length > 1) {
      getCitiesData();
      if(!searchedCities.includes(cityName)) {
        setSearchedCities([cityName, ...searchedCities]);
      }
    }
  } 

  const appendInput = (event) => {
    setCityName(event.target.value);
    getCitiesData();
  }

  return (
    <div className="bg-image">
      <div className="flex-container">
        {/* <SearchBar placeholder="Search By College Or City" onChange={appendInput}/>
          <datalist id="cityname">
            <option value="Boston"></option>
            <option value="Cambridge"></option>
          </datalist> */}
          <Dropdown
            placeholder='Search By College Or City'
            fluid
            search
            selection
            options={searchedCities}
            onChange={appendInput}
          />
        <SearchButton
          onClick={onSubmit}
          className="white"
        >
          <i className="fas fa-search mr10"></i>
          Search
        </SearchButton>
      </div>
      <ul>
        { cityList.length > 0 && cityList.map(city => <li>{city.name}</li>)}
      </ul>
      <ul>
        { searchedCities.length > 0 && searchedCities.map(cityName => <li style={{color:'red'}}>{cityName}</li>)}
      </ul>
    </div>
  );
}

export default App;
