import React, { useState } from 'react';
import './App.css';
import { getCities } from './helper';
import styled from 'styled-components';

const SearchBar = styled.input`
  margin: auto;
  width: 35%;
  height: 60%;
  background-color: white;
  position: absolute:
  top: 40%;
  padding-left: 20px;
  min-width: 200px;
  border: 0;
`;

const SearchButton = styled.button`
  margin: auto;
  width: 10%;
  height: 60%;
  background-color: pink;
  position: absolute:
  top: 40%;
  padding-left: 10px;
  border: 0;
  min-width: 100px;
  display: block;
`;

function App() {
  const [ cityName, setCityName ] = useState('');
  const [ cityList, setCityList ] = useState([]);
  const [ searchedCities, setSearchedCities ] = useState([]);

  const getCitiesData = () => {     
    if(cityName.length > 1) {
      getCities(cityName).then(res => setCityList(res)).catch(err => console.log(err));
    }
  }

  const onSubmit = () => {
    getCitiesData();
    setSearchedCities([...searchedCities, cityName])
  } 

  const appendInput = (event) => {
    setCityName(event.target.value);
    getCitiesData();
  }

  return (
    <div className="bg-image">
      <div className="flex-container">
        <SearchBar placeholder="Search By College Or City" onChange={appendInput}/>
        <SearchButton
          onClick={onSubmit}
        >
          <i className="fas fa-search mr10"></i>
          Search
        </SearchButton>
      </div>
      <ul>
        { cityList.length > 0 && cityList.map(city => <li>{city.name}</li>)}
      </ul>
    </div>
  );
}

export default App;
