import React, { useState } from 'react';
// import { Dropdown } from 'semantic-ui-react';
import './App.css';
import { getCities } from './helper';
import styled from 'styled-components';

const SearchBar = styled.input`
  width: 45%;
  background-color: white;
  position: absolute:
  top: 40%;
  min-width: 200px;
  border: none;
  border-radius: 3px 0 0 3px;
  padding: 20px;
  :focus{
    outline: none;
  }
`;

const SearchButton = styled.button`
  width: 15%;
  background-color: #e8415d;
  position: absolute:
  top: 40%;
  padding-left: 10px;
  border: none;
  min-width: 100px;
  display: block;
  font-color: white;
  border-radius: 2px 2px 0;
`;

function App() {
  const [ cityName, setCityName ] = useState('');
  const [ cityList, setCityList ] = useState([]);
  const [ searchedCities, setSearchedCities ] = useState([]);

  // const dropdown = document.getElementById('cityname');

  const getCitiesData = () => {     
    if(cityName.length > 1) {
      getCities(cityName.toLowerCase()).then(res => {
        setCityList(res);
      }).catch(err => console.log(err));
    }
  }

  const onSubmit = () => {
    if(cityName.length > 1) {
      getCitiesData();
      if(!searchedCities.includes(cityName)) {
        setSearchedCities([cityName, ...searchedCities]);
      }
    }
  } 

  const appendInput = (event) => {
    setCityName(event.target.value);
    getCitiesData();
  }

  return (
    <div className="bg-image">
      <div className="flex-container">
        <SearchBar placeholder="Search By College Or City" onChange={appendInput}/>
        {cityName && cityName.length && (
		    	<div className="search-clear v-center pointer" style={{ right: rightMargin }}>
		    		<i className="fa fa-times-circle icon-small text-tertiary" onClick={_ => setSuggestion('')} />
		    	</div>
		    )}

        <SearchButton
          onClick={onSubmit}
          className="white"
        >
          <i className="fas fa-search mr10"></i>
          Search
        </SearchButton>
      </div>
      <ul>
        { cityList.length > 0 && cityList.map(city => <li>{city.name}</li>)}
      </ul>
      <ul>
        { searchedCities.length > 0 && searchedCities.map(cityName => <li style={{color:'red'}}>{cityName}</li>)}
      </ul>
    </div>
  );
}

export default App;
