import axios from 'axios';

export const getCities = (cityName) => axios.get('https://base.amberstudent.com/api/v0/regions', 
    {   params: {
            sort_key: cityName,
            sort_order: 'desc',
            states: 'active',
            search_name: cityName
        }
    }
)
.then(res => console.log(res) || res.data.data)
.catch(err => console.log(err));
